        "author": "Mariia.Morozova.jetbrains.com",
        "authorFullName": "Mariia Morozova",
        "issueId": "WEB-16379",
        "parentId": null,
        "deleted": false,
        "jiraId": null,
        "text": "See screencast 'karma_terminal'.\nThe same performance if run using terminal: fast when Chrome is active and slow when don't.",
        "shownForIssueAuthor": false,
        "created": 1430384824014,
        "updated": null,
